using System;using System.Linq;using NUnit.Framework;namespace Keeper.OfScripts.Configuration.Tests{    [TestFixture]    public class KeeperConfigTests    {        [Test]        public void SectionTest()        {            var keeperConfig = KeeperConfig.Instance.Section;            Assert.IsNotNull(keeperConfig);        }                [Test]        public void AliasTest()        {            var keeperConfig = KeeperConfig.Instance.Section;            var alias = keeperConfig                .Aliases                .Cast<ResourceAliasElement>()                .FirstOrDefault(e => e.Alias == "jQuery");                                               Assert.IsTrue(keeperConfig.Aliases.Count > 0);            Assert.IsNotNull(alias);            Assert.AreEqual( "jQuery", alias.Alias);            Assert.AreEqual("remote", alias.Residence);            Assert.AreEqual("//ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js", alias.Source);        }    }}